// n8n Code node — parse_area (data-first) for ALL items (dedup + sort)

function parseAreaData(data) {
  const rows = Array.isArray(data) ? data : [];

  // 1) normaliza e deduplica por data (mantém o MAIOR valor do dia)
  const byDate = new Map();

  for (const r of rows) {
    const date = r?.createdAt;
    const val = Number(r?.maxinstalls);

    if (!date || !Number.isFinite(val)) continue;

    const prev = byDate.get(date);
    if (prev == null || val > prev) byDate.set(date, val);
  }

  // 2) transforma em série e ordena por data
  const series = Array.from(byDate.entries())
    .map(([x, y]) => ({ x, y }))
    .sort((a, b) => String(a.x).localeCompare(String(b.x)));

  if (series.length === 0) {
    return { series: [], metrics: null };
  }

  const first = series[0].y;
  const last = series[series.length - 1].y;

  const absoluteGrowth = last - first;
  const percentageGrowth = first ? absoluteGrowth / first : null;

  const avgDailyGrowth = absoluteGrowth / (series.length - 1 || 1);

  return {
    series,
    metrics: {
      firstValue: first,
      lastValue: last,
      absoluteGrowth,
      percentageGrowth,
      avgDailyGrowth,
      points: series.length,
      removedDuplicates: rows.length - series.length,
    }
  };
}

return $input.all().map(({ json }) => {
  // segurança: se cair aqui sem ready, não quebra
  if (json.status && json.status !== "ready") {
    const { data, appKey, payload, ...rest } = json;
    return { json: { ...rest, payload: null } };
  }

  const parsed = parseAreaData(json.data);

  const { data, appKey, payload, ...rest } = json;

  return {
    json: {
      ...rest,
      payload: parsed,
    }
  };
});

