// n8n Code node — build_analysis_area (payload já parseado)
// Entrada: widget kind="area" com payload { series:[{x,y}], metrics:{...} }
// Saída: adiciona json.analysis com features/preview

function safeArray(v) {
  return Array.isArray(v) ? v : [];
}

function toNumber(v) {
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
}

function round(n, d = 6) {
  if (!Number.isFinite(n)) return null;
  const p = 10 ** d;
  return Math.round(n * p) / p;
}

function seriesPreview(series, limit = 12) {
  const arr = safeArray(series);
  if (arr.length <= limit) return arr;
  const head = arr.slice(0, Math.ceil(limit / 2));
  const tail = arr.slice(arr.length - Math.floor(limit / 2));
  return [...head, { _truncated: true, total: arr.length }, ...tail];
}

function computeFallbackMetrics(series) {
  const points = series.length;
  if (!points) return { points: 0 };

  const first = series[0];
  const last = series[points - 1];

  const firstValue = toNumber(first?.y);
  const lastValue = toNumber(last?.y);

  const absoluteGrowth =
    (firstValue !== null && lastValue !== null) ? (lastValue - firstValue) : null;

  const percentageGrowth =
    (firstValue !== null && lastValue !== null && firstValue !== 0)
      ? round((lastValue - firstValue) / firstValue, 6)
      : null;

  const avgDailyGrowth =
    (absoluteGrowth !== null && points > 1) ? round(absoluteGrowth / (points - 1), 2) : null;

  let flatDays = 0;
  for (let i = 1; i < points; i++) {
    if (toNumber(series[i]?.y) === toNumber(series[i - 1]?.y)) flatDays++;
  }

  const ys = series.map(p => toNumber(p?.y)).filter(v => v !== null);
  const minValue = ys.length ? Math.min(...ys) : null;
  const maxValue = ys.length ? Math.max(...ys) : null;

  return {
    points,
    firstDate: first?.x ?? null,
    lastDate: last?.x ?? null,
    firstValue,
    lastValue,
    absoluteGrowth,
    percentageGrowth,
    avgDailyGrowth,
    minValue,
    maxValue,
    flatDays
  };
}

function last7Growth(series) {
  if (series.length < 14) return null;

  const a0 = toNumber(series[series.length - 14]?.y);
  const a1 = toNumber(series[series.length - 8]?.y);
  const b0 = toNumber(series[series.length - 7]?.y);
  const b1 = toNumber(series[series.length - 1]?.y);

  const g1 = (a0 !== null && a1 !== null) ? (a1 - a0) : null;
  const g2 = (b0 !== null && b1 !== null) ? (b1 - b0) : null;

  if (g1 === null || g2 === null) return null;

  return {
    prev7: round(g1, 2),
    last7: round(g2, 2),
    acceleration: round(g2 - g1, 2)
  };
}

return $input.all().map(({ json }) => {
  const base = { ...json };

  if (json.status !== "ready" || json.kind !== "area") {
    return { json: { ...base, analysis: null } };
  }

  const p = json.payload ?? {};
  const series = safeArray(p.series);

  // metrics vindo do parse_area (preferencial)
  const m = (p.metrics && typeof p.metrics === "object") ? p.metrics : null;

  const featuresFromParse = m ? {
    points: toNumber(m.points),
    firstValue: toNumber(m.firstValue),
    lastValue: toNumber(m.lastValue),
    absoluteGrowth: toNumber(m.absoluteGrowth),
    percentageGrowth: round(toNumber(m.percentageGrowth), 6),
    avgDailyGrowth: round(toNumber(m.avgDailyGrowth), 2),
    removedDuplicates: toNumber(m.removedDuplicates)
  } : null;

  const fallback = computeFallbackMetrics(series);

  // combina: o que vier do parse tem prioridade, mas completa com datas/min/max/flatDays via fallback
  const features = {
    points: featuresFromParse?.points ?? toNumber(fallback.points) ?? 0,
    firstDate: fallback.firstDate ?? (series[0]?.x ?? null),
    lastDate: fallback.lastDate ?? (series[series.length - 1]?.x ?? null),

    firstValue: featuresFromParse?.firstValue ?? fallback.firstValue ?? null,
    lastValue: featuresFromParse?.lastValue ?? fallback.lastValue ?? null,
    absoluteGrowth: featuresFromParse?.absoluteGrowth ?? fallback.absoluteGrowth ?? null,
    percentageGrowth: (featuresFromParse?.percentageGrowth ?? fallback.percentageGrowth) ?? null,
    avgDailyGrowth: (featuresFromParse?.avgDailyGrowth ?? fallback.avgDailyGrowth) ?? null,

    minValue: fallback.minValue ?? null,
    maxValue: fallback.maxValue ?? null,
    flatDays: fallback.flatDays ?? 0,

    removedDuplicates: featuresFromParse?.removedDuplicates ?? 0,
    last7Growth: last7Growth(series)
  };

  return {
    json: {
      ...base,
      analysis: {
        kind: "area",
        yKey: "y",
        features,
        preview: {
          series: seriesPreview(series, 12)
        }
      }
    }
  };
});
